mapper.select:
select qna_no, custid, qna_category, qna_title, qna_content, qna_hit, qna_date, qna_fname, qna_open, qna_answer, ticketid from (select rownum r, a.* from (select * from qna
where qna_category=#{category}
and ${searchColumn} like '%' || #{keyword} || '%'
where ${searchColumn} like '%' || #{keyword} || '%'
order by QNA_DATE desc) a) where r between #{startNo} and #{endNo}
